import React from 'react';
import { Encounter, Party } from '../types';

interface EncounterStatsProps {
  encounter: Encounter;
  party: Party;
}

interface OrganizationStats {
  [key: string]: {
    count: number;
    creaturesPerHero: number;
    recommended: {
      min: number;
      max: number;
      description: string;
    };
  };
}

interface RoleStats {
  [key: string]: number;
}

export const EncounterStats: React.FC<EncounterStatsProps> = ({ encounter, party }) => {
  // Calculate organization statistics
  const organizationStats: OrganizationStats = {};
  const roleStats: RoleStats = {};

  encounter.creatures.forEach(creature => {
    const org = creature.monster.organization;
    const role = creature.monster.role;

    // Organization stats
    if (!organizationStats[org]) {
      organizationStats[org] = {
        count: 0,
        creaturesPerHero: 0,
        recommended: getRecommendedPerHero(org)
      };
    }
    organizationStats[org].count += creature.quantity;

    // Role stats
    if (!roleStats[role]) {
      roleStats[role] = 0;
    }
    roleStats[role] += creature.quantity;
  });

  // Calculate creatures per hero for each organization
  Object.keys(organizationStats).forEach(org => {
    organizationStats[org].creaturesPerHero = organizationStats[org].count / party.heroCount;
  });

  const getRecommendedPerHero = (organization: string) => {
    switch (organization) {
      case 'minion':
        return { min: 2, max: 8, description: '2-8 minions per hero (groups of 4)' };
      case 'horde':
        return { min: 1, max: 2, description: '1-2 horde creatures per hero' };
      case 'platoon':
        return { min: 0.5, max: 1, description: '0.5-1 platoon creature per hero' };
      case 'elite':
        return { min: 0.5, max: 1, description: '0.5-1 elite creature per hero' };
      case 'leader':
        return { min: 0.25, max: 0.5, description: '0.25-0.5 leader per hero' };
      case 'solo':
        return { min: 0.17, max: 0.25, description: '0.17-0.25 solo per hero' };
      default:
        return { min: 0, max: 1, description: 'Varies by organization' };
    }
  };

  const getStatusColor = (current: number, recommended: { min: number; max: number }) => {
    if (current < recommended.min) return 'text-yellow-400'; // Too few
    if (current > recommended.max) return 'text-red-400'; // Too many
    return 'text-green-400'; // Good
  };

  const getStatusText = (current: number, recommended: { min: number; max: number }) => {
    if (current < recommended.min) return 'Too Few';
    if (current > recommended.max) return 'Too Many';
    return 'Good';
  };

  const totalCreatures = encounter.creatures.reduce((sum, c) => sum + c.quantity, 0);

  return (
    <div className="bg-gray-700 p-4 rounded-md">
      <h3 className="text-white font-semibold mb-4">Encounter Analysis</h3>

      {/* Basic Stats */}
      <div className="grid grid-cols-2 gap-4 mb-6 text-sm">
        <div>
          <p className="text-gray-300">Party ES: {party.encounterStrength}</p>
          <p className="text-gray-300">Encounter EV: {encounter.totalEV}</p>
        </div>
        <div>
          <p className="text-gray-300">Ratio: {(encounter.totalEV / party.encounterStrength).toFixed(2)}</p>
          <p className="text-gray-300">Total Creatures: {totalCreatures}</p>
        </div>
      </div>

      {/* Organization Analysis */}
      <div className="mb-6">
        <h4 className="text-white font-medium mb-3">Organization Analysis</h4>
        <div className="space-y-3">
          {Object.entries(organizationStats).map(([org, stats]) => (
            <div key={org} className="bg-gray-600 p-3 rounded">
              <div className="flex items-center justify-between mb-2">
                <span className="text-white font-medium capitalize">{org}</span>
                <span className={`text-sm font-medium ${getStatusColor(stats.creaturesPerHero, stats.recommended)}`}>
                  {getStatusText(stats.creaturesPerHero, stats.recommended)}
                </span>
              </div>
              <div className="text-gray-300 text-sm">
                <p>Count: {stats.count} creatures</p>
                <p>Per Hero: {stats.creaturesPerHero.toFixed(2)}</p>
                <p className="text-gray-400 text-xs">Recommended: {stats.recommended.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Organization Distribution Chart */}
      <div className="mb-6">
        <h4 className="text-white font-medium mb-3">Organization Distribution</h4>
        <div className="space-y-2">
          {Object.entries(organizationStats).map(([org, stats]) => {
            const percentage = (stats.count / totalCreatures) * 100;
            return (
              <div key={org} className="flex items-center gap-3">
                <div className="w-20 text-gray-300 text-sm capitalize">{org}</div>
                <div className="flex-1 bg-gray-600 rounded-full h-4">
                  <div 
                    className="bg-blue-500 h-4 rounded-full transition-all duration-300"
                    style={{ width: `${percentage}%` }}
                  ></div>
                </div>
                <div className="w-12 text-gray-300 text-sm text-right">{stats.count}</div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Role Distribution Chart */}
      <div className="mb-6">
        <h4 className="text-white font-medium mb-3">Role Distribution</h4>
        <div className="space-y-2">
          {Object.entries(roleStats).map(([role, count]) => {
            const percentage = (count / totalCreatures) * 100;
            return (
              <div key={role} className="flex items-center gap-3">
                <div className="w-20 text-gray-300 text-sm capitalize">{role}</div>
                <div className="flex-1 bg-gray-600 rounded-full h-4">
                  <div 
                    className="bg-green-500 h-4 rounded-full transition-all duration-300"
                    style={{ width: `${percentage}%` }}
                  ></div>
                </div>
                <div className="w-12 text-gray-300 text-sm text-right">{count}</div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Recommendations */}
      <div className="bg-gray-600 p-3 rounded">
        <h4 className="text-white font-medium mb-2">Recommendations</h4>
        <ul className="text-gray-300 text-sm space-y-1">
          {Object.entries(organizationStats).map(([org, stats]) => {
            const status = getStatusText(stats.creaturesPerHero, stats.recommended);
            if (status !== 'Good') {
              return (
                <li key={org} className="flex items-center gap-2">
                  <span className="text-red-400">•</span>
                  <span className="capitalize">{org}</span>: {status.toLowerCase()} - {stats.recommended.description}
                </li>
              );
            }
            return null;
          })}
          {Object.entries(organizationStats).every(([, stats]) => 
            getStatusText(stats.creaturesPerHero, stats.recommended) === 'Good'
          ) && (
            <li className="flex items-center gap-2">
              <span className="text-green-400">•</span>
              Organization balance looks good!
            </li>
          )}
        </ul>
      </div>
    </div>
  );
};
