import React, { useState } from 'react';
import { Monster } from '../types';

interface MonsterSearchProps {
  monsters: Monster[];
  onAddMonster: (monster: Monster) => void;
}

export const MonsterSearch: React.FC<MonsterSearchProps> = ({ monsters, onAddMonster }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedType, setSelectedType] = useState<string>('all');

  const filteredMonsters = monsters.filter(monster => {
    const matchesSearch = monster.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         monster.type.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = selectedType === 'all' || monster.type === selectedType;
    return matchesSearch && matchesType;
  });

  const uniqueTypes = ['all', ...Array.from(new Set(monsters.map(m => m.type)))];

  return (
    <div className="bg-gray-800 p-6 rounded-lg mb-6">
      <h2 className="text-xl font-bold text-white mb-4">Add Monsters</h2>

      <div className="flex flex-col md:flex-row gap-4 mb-4">
        <div className="flex-1">
          <label className="block text-sm font-medium text-gray-300 mb-2">
            Search Monsters
          </label>
          <input
            type="text"
            placeholder="Search by name or type..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </div>
        <div className="md:w-48">
          <label className="block text-sm font-medium text-gray-300 mb-2">
            Filter by Type
          </label>
          <select
            value={selectedType}
            onChange={(e) => setSelectedType(e.target.value)}
            className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {uniqueTypes.map(type => (
              <option key={type} value={type}>
                {type === 'all' ? 'All Types' : type}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="max-h-64 overflow-y-auto">
        {filteredMonsters.length === 0 ? (
          <p className="text-gray-400 text-center py-4">No monsters found</p>
        ) : (
          <div className="space-y-2">
            {filteredMonsters.map(monster => (
              <div
                key={monster.id}
                className="flex items-center justify-between p-3 bg-gray-700 rounded-md hover:bg-gray-600 transition-colors"
              >
                <div className="flex-1">
                  <h3 className="text-white font-medium">{monster.name}</h3>
                  <p className="text-gray-400 text-sm">
                    {monster.type} • Level {monster.level} • EV {monster.encounterValue}
                  </p>
                  <p className="text-gray-400 text-sm">
                    {monster.organization} • {monster.role}
                    {monster.organization === 'minion' && (
                      <span className="text-blue-400 ml-2">(Adds 4)</span>
                    )}
                  </p>
                </div>
                <button
                  onClick={() => onAddMonster(monster)}
                  className="ml-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
                >
                  {monster.organization === 'minion' ? 'Add 4' : 'Add'}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};
